from flask import Flask, render_template, request
import struct

app = Flask(__name__)

@app.route('/', methods=['GET', 'POST'])
def index():
    if request.method == 'POST':
        if request.form['action'] == 'ftoi':
            float_num = float(request.form['float_num'])
            int_num = bytearray(struct.pack('>f', float_num))
            result = ' '.join(format(x, '02x') for x in int_num)
            return render_template('index.html', result=result)
        elif request.form['action'] == 'itof':
            int_num = bytes.fromhex(request.form['int_num'])
            float_num = struct.unpack('>f', int_num)[0]
            result = '{:.2f}'.format(float_num)
            return render_template('index.html', result=result)
    return render_template('index.html')

