    justify-content: space-between;
    padding: 10px;
}

header h1 {
    font-size: 32px;
    margin: 0;
}

header a {
    color: #FFFFFF;
    text-decoration: none;
}

main {
    padding: 20px;
}

h1 {
    font-size: 24px;
    margin-bottom: 10px;
    font-family: 'Galindo', cursive;
    font-weight: normal;
}

ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

li {
    margin-bottom: 5px;
}

footer {
    background-color: #00838F;
    color: #FFFFFF;
    text-align: center;
    padding: 10px;
    position: fixed;
    bottom: 0;
    left: 0;
}

footer h1 {
    font-size: 16px;
    margin: 0;
}

@media screen and (max-width: 768px) {
    header h1 {
        font-size: 24px;
    }

    main {
        padding: 10px;
    }

    h1 {
        font-size: 20px;
    }

    footer h1 {
        font-size: 14px;
    }
} 

/* Shades of blue/teal */
header {
    background-color: #00838F;
}

footer {
    background-color: #00838F;
}

a:hover {
    color: #0288D1;
}

h1, li {
    color: #00ACC1;
}

@media screen and (max-width: 768px) {
    h1 {
        color: #00ACC1;
    }
}
