], function (Controller, Dialog, Table, Button, Text, Column, ColumnListItem) {
   "use strict";

   return Controller.extend("yourController", {
      onInit: function () {
         this.oDialog = new Dialog({
            title: "Table Dialog",
            contentWidth: "50%",
            content: new Table({
