```cpp
#include <cstdio>
#include <iostream>
#include <fstream>
#include <string>
#include <vector>

// Struct to store anchor information
struct Anchor {
    std::string id;
    std::string name;
    int posx;
    int posy;
    bool enabled;
};

// Struct to store control information
struct Control {
    int id;
    int type;
    std::string name;
    int x;
    int y;
    int width;
    int height;
    int anchor_id;
    std::string text;
};

// Struct to store window information
struct Window {
    int x;
    int y;
    int width;
    int height;
};

// Parses an integer from a string, returns true if successful
bool parseInt(const std::string& str, int& i) {
    try {
        i = std::stoi(str);
    } catch (...) {
        return false;
    }
    return true;
}

// Parses a boolean from a string, returns true if successful
bool parseBool(const std::string& str, bool& b) {
    if (str == "1") {
        b = true;
    } else if (str == "0") {
        b = false;
    } else {
        return false;
    }
    return true;
}

// Parses a control line from the .rgl file and returns a Control struct
Control parseControlLine(const std::string& line) {
    Control control;
    std::sscanf(line.c_str(), "c %d %d %s %d %d %d %d %d %s",
                &control.id, &control.type, &control.name[0], &control.x, &control.y, &control.width, &control.height, &control.anchor_id, &control.text[0]);
    return control;
}

// Parses an anchor line from the .rgl file and returns an Anchor struct
Anchor parseAnchorLine(const std::string& line) {
    Anchor anchor;
    std::sscanf(line.c_str(), "a %s %s %d %d %d", &anchor.id[0], &anchor.name[0], &anchor.posx, &anchor.posy, &anchor.enabled);
    return anchor;
}

int main() {
    std::ifstream input("layout.rgl");
    if (!input) {
        std::cerr << "Error: Could not open input file.\n";
        return 1;
    }

    std::string line;
    Window window = { 0, 0, -1, -1 };
    std::vector<Anchor> anchors;
    std::vector<Control> controls;

    // Parse the .rgl file line by line
    while (std::getline(input, line)) {
        // Ignore blank lines and comment lines
        if (line.empty() || line[0] == '#') {
            continue;
        }

        switch (line[0]) {
            case 'r': {
                // Parse window dimensions
                std::sscanf(line.c_str(), "r %d %d %d %d", &window.x, &window.y, &window.width, &window.height);
                break;
            }

            case 'a': {
                // Parse anchor information
                Anchor anchor = parseAnchorLine(line);
                anchors.push_back(anchor);
                break;
            }

            case 'c': {
                // Parse control information
                Control control = parseControlLine(line);
                controls.push_back(control);
                break;
            }

            default:
                std::cerr << "Error: Invalid line found.\n";
                return 2;
        }
    }

    // Print parsed information to console
    std::cout << "Window: [" << window.x << ", " << window.y << ", " << window.width << ", " << window.height << "]\n\n";
    std::cout << "Anchors:\n";
    for (const Anchor& anchor : anchors) {
        std::cout << "  Id: " << anchor.id << "\n";
        std::cout << "  Name: " << anchor.name << "\n";
        std::cout << "  Position: [" << anchor.posx << ", " << anchor.posy << "]\n";
        std::cout << "  Enabled: " << std::boolalpha << anchor.enabled << "\n";
        std::cout << "\n";
    }
    std::cout << "\n";

    std::cout << "Controls:\n";
    for (const Control& control : controls) {
        std::cout << "  Id: " << control.id << "\n";
        std::cout << "  Type: " << control.type << "\n";
        std::cout << "  Name: " << control.name << "\n";
        std::cout << "  Rectangle: [" << control.x << ", " << control.y << ", " << control.width << ", " << control.height << "]\n";
        std::cout << "  Anchor ID: " << control.anchor_id << "\n";
        std::cout << "  Text: " << control.text << "\n";
        std::cout << "\n";
    }

    return 0;
}
```

This program reads the .rgl file line by line, parses each line into its component parts, and stores the resulting data in a vector of structs for anchors and controls. It also prints the parsed data to the console for verification.

