private Color HSLtoRGB(int h, double s, double l)
{
    double r, g, b;
    double c, x, m;

    c = (1 - Math.Abs(2 * l - 1)) * s;
    x = c * (1 - Math.Abs((h / 60) % 2 - 1));
    m = l - c / 2;

    if (h >= 0 && h < 60)
    {
        r = c;
        g = x;
        b = 0;
    }
    else if (h >= 60 && h < 120)
    {
        r = x;
        g = c;
        b = 0;
    }
    else if (h >= 120 && h < 180)
    {
        r = 0;
        g = c;
        b = x;
    }
    else if (h >= 180 && h < 240)
