script type="text/javascript">
$(function () {

    var curImageIdx = 1, total = 752
    var GroupId= 2090518
    var wrapper = $('#image-gallery'), curSpan = wrapper.find('.current');
    var viewer = ImageViewer(wrapper.find('.image-container'));

    wrapper.find('.total').html(total);

    $(".current").keyup(function (event) {
        if (event.keyCode === 13) {
            $("#id_of_button").click();

            curImageIdx = $(this).val();

            if (curImageIdx > total) curImageIdx = 1;
            if (curImageIdx < 1) curImageIdx = total;
            showImage();
        }
    });


    function showImage(){
       // var imgObj = images[curImageIdx - 1];
        //viewer.load(imgObj.small, imgObj.big);
        //var address = $("#txtAddress").val();
        //$(".image-container").find('.iv-loader').show();
        $("body").find('.LoadingBox').show();
        var gid = GroupId.toString();
        var pid = (parseInt(curImageIdx) - 1).toString();
        $(".lnkDownload").attr("href","/CollectionPdf.aspx?GroupID="+gid+"&Mode=1&PageID="+pid)
        $.ajax({
            type: "POST",
            url: "ShowPic.aspx/ImageLoad",
            data: "{'pageID':'" + pid + "','GroupId':'" + gid + "'}",
            contentType: "application/json; charset=utf-8",
            dataType: "text",
            success: function (output) {
                var obj = jQuery.parseJSON(output);
                viewer.load(obj.d.split("#")[1], obj.d.split("#")[0]);

            }, complete: function ()
            {
                $("body").find('.LoadingBox').hide();

            }
        });


        curSpan.val(curImageIdx);
    }

    wrapper.find('.next').click(function(){
        curImageIdx++;

        if(curImageIdx > total) curImageIdx = 1;
        showImage();
    });

    wrapper.find('.prev').click(function(){
         curImageIdx--;
        if(curImageIdx < 1) curImageIdx = total;
        showImage();
    });

    //initially show image
    showImage();
});
</script>
