      border-left: 1px solid lightgray;
      border-bottom: 1px solid lightgray;
      min-width: 100px;
      height: 38px;
    }

    .table th {
      background-color: #eee;
    }

    .table th,
    .table td {
      text-align: center;
    }

    .title th {
      font-size: 15px;
      padding: 10px;
    }

    .no-border {
      border: none;
    }

    .only-left-border {
      border-top: none;
      border-bottom: none;
      border-right: none;
    }

    .only-bottom-border {
      border-top: none;
      border-left: none;
      border-right: none;
    }

    .only-left-bottom-border {
      border-top: none;
      border-right: none;
    }
  </style>
  <script>
    window.onload = function () {
      setWidth(document.getElementsByTagName('td'))
      setWidth(document.getElementsByTagName('th'))

      table2Excel()
    }

    function setWidth(tds) {
      for (var i = 0; i < tds.length; i++) {
        var td = tds[i]
        var colspan = td.getAttribute('colspan') || 1
        td.setAttribute('width', (100 / 12 * colspan) + '%')
      }
    }

    function table2Excel() {
      var workbook = XLSX.utils.table_to_book(document.getElementById('ECR'));
      var binaryData = XLSX.write(workbook, { bookType: 'xlsx', type: 'binary' });

      function s2ab(s) {
        var buf = new ArrayBuffer(s.length);
        var view = new Uint8Array(buf);
        for (var i = 0; i < s.length; i++) view[i] = s.charCodeAt(i) & 0xFF;
        return buf;
      }

      var filename = (document.title || 'ECR') + '-' + new Date().getTime() + '.xlsx';

      var link = document.createElement('a');
      link.href = URL.createObjectURL(new Blob([s2ab(binaryData)], { type: "application/octet-stream" }));
      link.download = filename;
      link.click();
    }
  </script>
</head>
<body class="white-bg">
  <div class="wrapper wrapper-content animated fadeInRight ibox-content" th:object="${ecrRequest}">
    <div>
      <p>Hi <span th:text="${receiver}">Lisa</span>,<br /></p>
      <p>
        <span th:text="${subject}"></span>. Click the <a
          th:href="@{'http://'+${host}+':'+${port}+'/ecr/request'}">Link</a> to see detail.
      </p>
    </div>
    <table id="ECR">
      <!-- Your HTML table content -->
    </table>
  </div>
</body>
</html>
```

Make sure to include the `xlsx.core.min.js` and `xlsx.export.min.js` files in the same directory as your HTML file. When you load the HTML page, it will automatically trigger the `table2Excel` function, which will convert the table to an Excel file and initiate the download.
