  const handleMultiOnChange = (e: any) => {
    setAuthSuccess(false);
    setKeyword("");
    const dedicatedNumbers = groupedOptions[0] ?? [];
    const sharedNumbers = groupedOptions[1] ?? [];

    if (e.length === 0) {
      setAvailableSenders(groupedOptions);
      setIsSelectedNumberShared(false);
    }

    if (e[0] && e[0].type === "dedicated") {
      const updatedArr = [
        dedicatedNumbers,
        {
          ...sharedNumbers,
          options: sharedNumbers.options.map((number) => {
            return { ...number, isDisabled: true };
          }),
        },
      ];

      setAvailableSenders(updatedArr);
      setIsSelectedNumberShared(false);
      setSelectedSenders(e);
    }

    if (e[0] && e[0].type === "shared") {
      const updatedArray = [
        {
          ...dedicatedNumbers,
          options: dedicatedNumbers.options.map((item) => ({
            ...item,
            isDisabled: true,
          })),
        },
        {
          ...sharedNumbers,
          options: sharedNumbers.options.reduce((acc, val) => {
            if (val.value !== e[0].value) {
              const updatedDisabledNumber = { ...val, isDisabled: true };
              acc.push(updatedDisabledNumber);
            }

            return acc;
          }, [] as SendersOptions[]),
        },
      ];

      setAvailableSenders(updatedArray);
      setSelectedSenders(e);
      setIsSelectedNumberShared(true);
    }
  };

  return (
    <Select
      closeMenuOnSelect={false}
      components={makeAnimated()}
      isMulti
      options={availableSenders}
      placeholder={placeholder}
      onChange={handleMultiOnChange}
    />
  );
