library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;
 
entity UART_TX is
  generic (
    g_CLKS_PER_BIT : integer := 115     -- Needs to be set correctly
    );
  port (
    i_Clk       : in  std_logic;
    i_TX_DV     : in  std_logic;
    i_TX_Byte   : in  std_logic_vector(7 downto 0);
    o_TX_Active : out std_logic;
    o_TX_Serial : out std_logic;
    o_TX_Done   : out std_logic
    );
end UART_TX;
 
 
architecture RTL of UART_TX is
 
  type t_SM_Main is (s_Idle, s_TX_Start_Bit, s_TX_Data_Bits,
                     s_TX_Stop_Bit, s_Cleanup);
  signal r_SM_Main : t_SM_Main := s_Idle;
 
  signal r_Clk_Count : integer range 0 to g_CLKS_PER_BIT-1 := 0;
  signal r_Bit_Index : integer range 0 to 7 := 0;  -- 8 Bits Total
  signal r_TX_Data   : std_logic_vector(7 downto 0) := (others => '0');
  signal r_TX_Done   : std_logic := '0';
   
begin
 
   
  p_UART_TX : process (i_Clk)
  begin
    if rising_edge(i_Clk) then
         
      case r_SM_Main is
 
        when s_Idle =>
          o_TX_Active <= '0';
          o_TX_Serial <= '1';         -- Drive Line High for Idle
          r_TX_Done   <= '0';
          r_Clk_Count <= 0;
          r_Bit_Index <= 0;
 
          if i_TX_DV = '1' then
            r_TX_Data <= i_TX_Byte;
            r_SM_Main <= s_TX_Start_Bit;
          else
            r_SM_Main <= s_Idle; end if; -- Send out Start Bit. Start bit = 0 when s_TX_Start_Bit =>
          o_TX_Active <= '1';
          o_TX_Serial <= '0';
 
          -- Wait g_CLKS_PER_BIT-1 clock cycles for start bit to finish
          if r_Clk_Count < g_CLKS_PER_BIT-1 then
            r_Clk_Count <= r_Clk_Count + 1;
            r_SM_Main   <= s_TX_Start_Bit;
          else
            r_Clk_Count <= 0;
            r_SM_Main   <= s_TX_Data_Bits; end if; -- Wait g_CLKS_PER_BIT-1 clock cycles for data bits to finish when s_TX_Data_Bits =>
          o_TX_Serial <= r_TX_Data(r_Bit_Index);
           
          if r_Clk_Count < g_CLKS_PER_BIT-1 then
            r_Clk_Count <= r_Clk_Count + 1;
            r_SM_Main   <= s_TX_Data_Bits;
          else
            r_Clk_Count <= 0;
             
            -- Check if we have sent out all bits
            if r_Bit_Index < 7 then
              r_Bit_Index <= r_Bit_Index + 1;
              r_SM_Main   <= s_TX_Data_Bits;
            else
              r_Bit_Index <= 0;
              r_SM_Main   <= s_TX_Stop_Bit; end if; end if; -- Send out Stop bit. Stop bit = 1 when s_TX_Stop_Bit =>
          o_TX_Serial <= '1';
 
          -- Wait g_CLKS_PER_BIT-1 clock cycles for Stop bit to finish
          if r_Clk_Count < g_CLKS_PER_BIT-1 then
            r_Clk_Count <= r_Clk_Count + 1;
            r_SM_Main   <= s_TX_Stop_Bit;
          else
            r_TX_Done   <= '1';
            r_Clk_Count <= 0;
            r_SM_Main   <= s_Cleanup; end if; -- Stay here 1 clock when s_Cleanup =>
          o_TX_Active <= '0';
          r_TX_Done   <= '1';
          r_SM_Main   <= s_Idle; when others =>
          r_SM_Main <= s_Idle;
 
      end case;
    end if;
  end process p_UART_TX;
 
  o_TX_Done <= r_TX_Done;
   
end RTL;

#include "STM32F10x.h"
#include <stdbool.h>

#define bits 7
#define rxdPin 6	

uint16_t rData, rdCount;
bool rxDataReady, rxBusy;

void PwrRccInit(void);
void GPIOInit(void);
void gpioIntInit(void);
void timer3Init(void);
void receive7Init(void);

int main(){

	PwrRccInit();
	GPIOInit();
	receive7Init();

	while(1){
		if(rxDataReady==1){
			GPIOA->ODR = rData;
			rxDataReady=0;
			}
		}
	}

void receive7Init(void){
	rxBusy=0;
	rxDataReady=0;
	gpioIntInit();
	timer3Init();
	}

void PwrRccInit(void){
	PWR->CR  = 0x00000000;
	PWR->CSR = 0x00000000;

	RCC->CR   =   0x00000083;
	RCC->CFGR =   0x00000000;
	RCC->CIR  =   0x00000000;
	}

void GPIOInit(void){
	RCC->APB2ENR   =   0x0000000c ;
	RCC->APB2RSTR |=   0x0000000c ;
	RCC->APB2RSTR &= ~(0x0000000c);
	GPIOA->CRL     =   0x22222222 ;
	GPIOA->CRH     =   0x44444444 ;
	GPIOB->CRL     =   0x44442222 ;
	GPIOB->CRH     =   0x44444444 ;
	}

void timer3Init(void){
	RCC->APB1RSTR |= 0x0002;	//for timer 3
	RCC->APB1RSTR &= ~(0x0002);
	RCC->APB1ENR |=0x0002;//RCC_APB1ENR_TIM3EN
	TIM3->CR2 = 0x000;
	TIM3->SMCR = 0x000;
	TIM3->DIER = 0x001;
	TIM3->PSC = (2-1);//prescaler
	TIM3->ARR = (417-1);//auto-reload
	TIM3->CCR4 = 3906/100;
	TIM3->CCMR2 = 0xFC00;
	TIM3->CCER = 0x0000;
	TIM3->CR1 = 0x000;	//disable
	NVIC_EnableIRQ(TIM3_IRQn);
	}

void TIM3_IRQHandler(void){
	uint16_t a;
	TIM3->SR &= ~0x00000001;
	TIM3->ARR = (417-1);//auto-reload
	a=GPIOB->IDR&0x0040;//1<<rxdPin
	rData|=(a>>rdCount);
	if(rdCount!=0){
		rdCount--;
		}
	else{
		rxBusy = 0;
		rxDataReady=1;
		TIM3->CR1 = 0x000;	//disable
		EXTI->PR = (1<<rxdPin);				//clear pending interrupt
		EXTI->IMR  = (1<<rxdPin);//falling edge interrupt ensabled
		}
	}

void gpioIntInit(void){	//GPIO pin Interrrupt
	RCC->APB2ENR|=0x0001;
	AFIO->EXTICR[1]=0x0100;	//interrupt from port B pin 6 = rxdPin
	EXTI->IMR   = (1<<rxdPin);		//bit to generate interrup is defined by rxdPin
	EXTI->EMR   = 0<<rxdPin;			//no event
	EXTI->RTSR  = 0<<rxdPin;	//rising edge(no)
	EXTI->FTSR  = (1<<rxdPin);			//falling edge interrupt
	EXTI->PR    = (1<<rxdPin);				//clear pending interrupts
	EXTI->SWIER = 0<<rxdPin;		//no software interrupt
	NVIC_EnableIRQ(EXTI9_5_IRQn);
	}

void EXTI9_5_IRQHandler(void){
	EXTI->PR = (1<<rxdPin);				//clear pending interrupt
	TIM3->ARR = (625-1);//auto-reload = 1.5 bit
	EXTI->IMR  = 0x000;//interrupt disabled
	TIM3->CR1 = 0x001;	//enable
	rdCount=bits-1;
	rData=0;
	rxBusy=1;
	}

import Graph from "graphology";
import Sigma from "sigma";
import chroma from "chroma-js";
import { v4 as uuid } from "uuid";
import ForceSupervisor from "graphology-layout-force/worker";

// Retrieve the html document for sigma container
const container = document.getElementById("sigma-container") as HTMLElement;

// Create a sample graph
const graph = new Graph();
graph.addNode("n1", { x: 0, y: 0, size: 10, color: chroma.random().hex() });
graph.addNode("n2", { x: -5, y: 5, size: 10, color: chroma.random().hex() });
graph.addNode("n3", { x: 5, y: 5, size: 10, color: chroma.random().hex() });
graph.addNode("n4", { x: 0, y: 10, size: 10, color: chroma.random().hex() });
graph.addEdge("n1", "n2");
graph.addEdge("n2", "n4");
graph.addEdge("n4", "n3");
graph.addEdge("n3", "n1");

// Create the spring layout and start it
const layout = new ForceSupervisor(graph, { isNodeFixed: (_, attr) => attr.highlighted });
layout.start();

// Create the sigma
const renderer = new Sigma(graph, container);

//
// Drag'n'drop feature
// ~~~~~~~~~~~~~~~~~~~
//

// State for drag'n'drop
let draggedNode: string | null = null;
let isDragging = false;

// On mouse down on a node
//  - we enable the drag mode
//  - save in the dragged node in the state
//  - highlight the node
//  - disable the camera so its state is not updated
renderer.on("downNode", (e) => {
  isDragging = true;
  draggedNode = e.node;
  graph.setNodeAttribute(draggedNode, "highlighted", true);
});

// On mouse move, if the drag mode is enabled, we change the position of the draggedNode
renderer.getMouseCaptor().on("mousemovebody", (e) => {
  if (!isDragging || !draggedNode) return;

  // Get new position of node
  const pos = renderer.viewportToGraph(e);

  graph.setNodeAttribute(draggedNode, "x", pos.x);
  graph.setNodeAttribute(draggedNode, "y", pos.y);

  // Prevent sigma to move camera:
  e.preventSigmaDefault();
  e.original.preventDefault();
  e.original.stopPropagation();
});

// On mouse up, we reset the autoscale and the dragging mode
renderer.getMouseCaptor().on("mouseup", () => {
  if (draggedNode) {
    graph.removeNodeAttribute(draggedNode, "highlighted");
  }
  isDragging = false;
  draggedNode = null;
});

// Disable the autoscale at the first down interaction
renderer.getMouseCaptor().on("mousedown", () => {
  if (!renderer.getCustomBBox()) renderer.setCustomBBox(renderer.getBBox());
});

//
// Create node (and edge) by click
// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
//

// When clicking on the stage, we add a new node and connect it to the closest node
renderer.on("clickStage", ({ event }: { event: { x: number; y: number } }) => {
  // Sigma (ie. graph) and screen (viewport) coordinates are not the same.
  // So we need to translate the screen x & y coordinates to the graph one by calling the sigma helper `viewportToGraph`
  const coordForGraph = renderer.viewportToGraph({ x: event.x, y: event.y });

  // We create a new node
  const node = {
    ...coordForGraph,
    size: 10,
    color: chroma.random().hex(),
  };
  // Searching the two closest nodes to auto-create an edge to it
  const closestNodes = graph
    .nodes()
    .map((nodeId) => {
      const attrs = graph.getNodeAttributes(nodeId);
      const distance = Math.pow(node.x - attrs.x, 2) + Math.pow(node.y - attrs.y, 2);
      return { nodeId, distance };
    })
    .sort((a, b) => a.distance - b.distance)
    .slice(0, 2);

  // We register the new node into graphology instance
  const id = uuid();
  graph.addNode(id, node);

  // We create the edges
  closestNodes.forEach((e) => graph.addEdge(id, e.nodeId));
});

