```javascript
onInit: function() {
    var oModel = new sap.ui.model.xml.XMLModel();

    oModel.loadData("metadata.xml"); // Load the metadata file

    oModel.attachRequestCompleted(function() {
        var oEntries = oModel.getProperty("/feed/entry");

        var oItemsSetLink = oEntries[0].link.find((link) => link.getAttribute("rel") === "http://schemas.microsoft.com/ado/2007/08/dataservices/related/ItemsSet").getAttribute("href");

        var oItemsSet = oEntries[0].content;

        var oItemsData = Array.from(oItemsSet.getElementsByTagName("m:properties")).map((properties) => {
            return Array.from(properties.children).reduce((obj, prop) => {
                obj[prop.tagName.split(":")[1]] = prop.textContent;
                return obj;
            }, {});
        });

        var oSmartTable = this.getView().byId("smartTable");

        var oTable = new sap.ui.table.Table({
            columns: [ // Define the columns for the table
                new sap.ui.table.Column({
