@Composable
private fun RenderData(data: String) {

    val pattern = Regex("""^(\w+\S*)\(([^;]+);;([^;]+)\)$""")

    val lines = data.split("\n")

    for (line in lines) {

        val matchResult = pattern.find(line)

        if (matchResult != null) {
            val functionName = matchResult.groupValues[1]
            val firstParam = matchResult.groupValues[2]
            val secondParam = matchResult.groupValues[3]

            when (functionName) {
                "Text" -> Text(text = firstParam)
                "Image" -> Image(
                    painter = painterResource(id = firstParam.toInt()),
                    contentDescription = secondParam
                )
            }
        }
    }
}

