```
private fun sortRows(rows: List<TableRow>, sortBy: String, ascending: Boolean): List<TableRow> {
    return when (sortBy) {
        "title" -> {
            val comparator = compareBy<TableRow> { (it.getChildAt(2) as TextView).text.toString() }
            val sortedRows = rows.subList(1, rows.size).sortedWith(comparator)
