Object.keys(stationInfo).forEach(key => {
  const imgElement = document.createElement('img');
  imgElement.src = stationInfo[key].match(/src="([^"]+)"/)[1]; // Extracting the image source URL
  imgElement.alt = stationInfo[key].match(/alt="([^"]+)"/)[1]; // Extracting the alt text
