  }

  for (let i = 0; i < paths.length; i++) {
    const { nodes, startNode, endNode, cost } = paths[i];
    if (startNode.x === enemyX && startNode.y === enemyY) {
      path = { nodes: nodes, cost: cost };
      break;
    }
  }

  if (path) {
    const followNode = path.nodes.shift();
    const theta = Math.atan2(followNode.y - enemyY, followNode.x - enemyX);
    enemySpeedX = Math.cos(theta) * 5;
    enemySpeedY = Math.sin(theta) * 5;
    enemyX += enemySpeedX;
    enemyY += enemySpeedY;
    console.log(enemyX, enemyY);

    if (
      enemyX - ENEMY_RADIUS < 0 ||
      enemyX + ENEMY_RADIUS > canvas.width ||
      enemyY - ENEMY_RADIUS < 0 ||
      enemyY + ENEMY_RADIUS > canvas.height
    ) {
      paths = paths.filter(
        (p) => p.startNode.x !== enemyX || p.startNode.y !== enemyY
      );
      return;
    }

    context.fillStyle = "blue";
    context.fillRect(
      enemyX - ENEMY_RADIUS,
      enemyY - ENEMY_RADIUS,
      ENEMY_RADIUS * 2,
      ENEMY_RADIUS * 2
    );

    if (path.nodes.length === 0) {
      paths = paths.filter(
        (p) => p.startNode.x !== enemyX || p.startNode.y !== enemyY
      );
    }
  } else {
    paths = paths.filter(
      (p) => p.startNode.x !== enemyX || p.startNode.y !== enemyY
    );
  }
}

function checkCollision() {
  let isOnGround = false;

  if (playerY + PLAYER_RADIUS >= canvas.height) {
    playerSpeedY = 0;
    playerY = canvas.height - PLAYER_RADIUS;
    playerIsOnGround = true;
    isOnGround = true;
  }
  if (
    playerX + 50 >= enemyX - ENEMY_RADIUS &&
    playerX <= enemyX + ENEMY_RADIUS &&
    playerY <= enemyY + ENEMY_RADIUS &&
    playerY + 50 >= enemyY - ENEMY_RADIUS
  ) {
    playerSpeedY = 0;
    playerY = enemyY - ENEMY_RADIUS - 50;
    playerIsOnGround = true;
    isOnGround = true;
  }
  if (playerY <= 0) {
    playerSpeedY = 0;
    playerY = 0;
  }
  if (!isOnGround) {
    playerIsOnGround = false;
  }
}

function draw() {
  context.clearRect(0, 0, canvas.width, canvas.height);
  context.fillStyle = "gray";
  context.fillRect(0, 0, canvas.width, canvas.height);

  applyGravity();
  applyTraction();
  movePlayer();
  moveEnemy();
  checkCollision();

  requestAnimationFrame(draw);
}

function reset() {
  playerX = 50;
  playerY = 550;
  enemyX = 750;
  enemyY = 150;
  enemySpeedX = 0;
  enemySpeedY = 0;
  playerSpeedX = 0;
