```javascript
onSortByDatePressed: function (oEvent) {
  var oList = this.getView().byId("idList");
  var oBinding = oList.getBinding("items");

  // Check if a sorter is already applied
  var bSortActive = oBinding.aSorters.length > 0;

  if (!bSortActive) {
    // No sorter applied, sort by "assign_modified_date" in descending order
    var oSorter = new sap.ui.model.Sorter("assign_modified_date", true);
    oBinding.sort(oSorter);

    // Store the sort order
    oList.data("sortOrder", "desc");
  } else {
    // Get the current sort order
    var sSortOrder = oList.data("sortOrder");

    if (sSortOrder === "asc") {
      // Sort by "assign_modified_date" in descending order
      oBinding.sort(new sap.ui.model.Sorter("assign_modified_date", true));

      // Update the sort order
      oList.data("sortOrder", "desc");
    } else {
      // Sort by "assign_modified_date" in ascending order
      oBinding.sort(new sap.ui.model.Sorter("assign_modified_date", false));

      // Update the sort order
      oList.data("sortOrder", "asc");
    }
  }
},
```

