  var sSortKey = "sortOrder_" + sProperty;

  // Check if a sorter is already applied
  var bSortActive = oBinding.aSorters.length > 0;

  if (!bSortActive) {
    // No sorter applied, sort by specified property in descending order
    var oSorter = new sap.ui.model.Sorter(sProperty, true);
    oBinding.sort(oSorter);

    // Store the sort order for the specified property
    oList.data(sSortKey, "desc");
  } else {
    // Get the current sort order for the specified property
    var sSortOrder = oList.data(sSortKey);

    if (sSortOrder === "asc") {
      // Sort by specified property in descending order
      oBinding.sort(new sap.ui.model.Sorter(sProperty, true));

      // Update the sort order for the specified property
      oList.data(sSortKey, "desc");
    } else {
      // Sort by specified property in ascending order
      oBinding.sort(new sap.ui.model.Sorter(sProperty, false));

      // Update the sort order for the specified property
      oList.data(sSortKey, "asc");
    }
  }
},
```

