import { gStyle } from '../styles/style';
import { firebase } from '../Firebase/firebase';
import React, {useState, useEffect} from 'react';

const options = { day: 'numeric', month: 'long', hour: 'numeric', minute: 'numeric' };

export default function BookedMK(props) {
    const {mk, userId, onDelete, index} = props;
    const {time, ...mkData}=mk;
    let formattedTime=null;
    if(time?.seconds){
        formattedTime = new Date(time.seconds * 1000).toLocaleTimeString([], options);
    }

    const handleDeleteBookedMK=()=>{
        const userRef=firebase.database().ref(`users/${userId}/bookedMK/${mk.index}`);
        userRef.remove().then(()=>{
            onDelete(mk.index);
        }).catch((error)=>{
            console.error(error);
        })
    }

  return (
        <View style={gStyle.bookedMK}>
            <Text style={gStyle.ProfileLine}></Text>
                <View key={index} style={gStyle.ProfileDetails}>
                <Image source={{uri:mkData.image}} style={gStyle.ProfileImg}/>
                <View style={gStyle.ProfileDescription}>
                    <Text style={gStyle.ProfileTitleOfMK}>{mkData.name}</Text>
                    <Text style={gStyle.ProfileDate}>{formattedTime}</Text>
                    <Text style={gStyle.ProfilePrice}>Цена: {mkData.price} Р.</Text>
                </View>
            </View>
            <Pressable
            onPress={handleDeleteBookedMK}
            >
                <Text>Убрать</Text>
            </Pressable>
            <Text style={gStyle.ProfileLine}></Text>
        </View>

  );
}


