                var cx = Entity.getX(entities[i]);
                var cy = Entity.getY(entities[i]) + height / 2;
                var cz = Entity.getZ(entities[i]);

                // Set the new collision box
                Entity.setCollisionSize(entities[i], width, height);

                // Update the entity's position if needed
                Entity.setPosition(entities[i], cx, cy, cz);
