            int u = pq.top().second;
            pq.pop();

            visited[u] = true;

            for(auto i = adj[u].begin(); i != adj[u].end(); i++) {
                int v = (*i).first;
                int weight = (*i).second;

                if(!visited[v] && key[v] > weight) {
                    key[v] = weight;
                    pq.push(make_pair(key[v], v));
                    parent[v] = u;
                }
            }
        }

        cout << "Minimum Spanning Tree using Prim's Algorithm:\n";
        for(int i = 1; i < V; i++) {
            cout << parent[i] << " - " << i << " : " << key[i] << endl;
        }
    }

