        pq.push(make_pair(0, src));
        key[src] = 0;

        while(!pq.empty()) {
            int u = pq.top().second;
            pq.pop();

            visited[u] = true;

            for(auto i = edges.begin(); i != edges.end(); i++) {
                int v = i->second.second;
                int weight = i->first;

                if(u == i->second.first && !visited[v] && key[v] > weight) {
                    key[v] = weight;
                    pq.push(make_pair(key[v], v));
                    parent[v] = u;
                }
            }
        }

        cout << "Minimum Spanning Tree using Prim's Algorithm:\n";
        for(int i = 1; i < V; i++) {
            cout << parent[i] << " - " << i << " : " << key[i] << endl;
        }
    }


    void kruskal() {
        vector<pair<int, pair<int, int> > > MST;
        vector<int> parent(V, -1);

        sort(edges.begin(), edges.end());
        int edgeCount = 0;

        for(auto i = edges.begin(); i != edges.end(); i++) {
            int u = i->second.first;
            int v = i->second.second;
            int weight = i->first;

            int set_u = find(parent, u);
            int set_v = find(parent, v);

            if(set_u != set_v) {
                MST.push_back(make_pair(weight, make_pair(u, v)));
                unionset(parent, set_u, set_v);
                edgeCount++;
                if(edgeCount == V - 1) {
                    break;
                }
            }
        }

        cout << "Minimum Spanning Tree using Kruskal's Algorithm:\n";
        for(auto i = MST.begin(); i != MST.end(); i++) {
            cout << i->second.first << " - " << i->second.second << " : " << i->first << endl;
        }
    }
};

