   // Split the work among the threads
   const int chunk_size = (end - start + 1) / num_threads;
   std::thread threads[num_threads];
   for (int i = 0; i < num_threads-1; ++i) {
       const int thread_start = start + i * chunk_size;
       const int thread_end = thread_start + chunk_size - 1;
       threads[i] = std::thread(loop, thread_start, thread_end);
   }
   // The last thread may have to handle leftover work
   threads[num_threads-1] = std::thread(loop, start + (num_threads-1) * chunk_size, end);

   // Wait for the threads to finish
   for (int i = 0; i < num_threads; ++i) {
       threads[i].join();
   }
   return 0;
}
```

