namespace Project.Controllers
{
    public class AuthController : Controller
    {
        //testing vs
        IAuthService _authService;
        public AuthController(IAuthService authService)
        {
            _authService = authService;
        }
        public IActionResult Index()
        {
            return View();
        }

        [HttpPost]
        public IActionResult Login(UserLoginDto loginDto,string returnUrl) 
        {
            var user=_authService.Login(loginDto);
            if(user != null)
            {
                var token=_authService.CreateAccessToken(user);
                AddToCookie("X-Access-Token", token.Token, token.Expiration);
                AddToCookie("X-Refresh-Token", token.RefreshToken,DateTime.Now.AddDays(7));
                if (Url.IsLocalUrl(returnUrl))
                {
                    return Redirect(returnUrl);
                }
                return Redirect("/");
            }
            ViewData["returnUrl"]=returnUrl;
            return Login(null);
        }
        [HttpPost]
        public IActionResult Register(UserRegisterDto registerDto)
        {
            if(ModelState.IsValid)
            {
                if (!_authService.IsUserExist(registerDto.Email))
                {
                    var user = _authService.Register(registerDto);
                    if (user != null)
                    {
                        var token = _authService.CreateAccessToken(user);
                        AddToCookie("X-Access-Token", token.Token, token.Expiration);
                        AddToCookie("X-Refresh-Token", token.RefreshToken, DateTime.Now.AddDays(7));
                        return Redirect("/");
                    }
                }
            }
            return Register();
        }
        public IActionResult RefreshToken(string returnUrl)
        {
            string refreshToken;
            if ((refreshToken = HttpContext.Request.Cookies["X-Refresh-Token"]) is not null){
                var user = _authService.RefreshToken(refreshToken);
                if (user != null)
                {
                    var token = _authService.CreateAccessToken(user);
                    AddToCookie("X-Access-Token", token.Token, token.Expiration);
                    AddToCookie("X-Refresh-Token", token.RefreshToken, DateTime.Now.AddDays(7));
                    if(Url.IsLocalUrl(returnUrl))
                    {
                        return Redirect(returnUrl);
                    }

                }
            }
            return Redirect("/");

        }

        public IActionResult Logout()
        {
            if (HttpContext.User.Identities.Any())
            {
                AddToCookie("X-Access-Token", "", DateTime.Now.AddDays(-1));
                AddToCookie("X-Refresh-Token", "", DateTime.Now.AddDays(-1));
            }
            return Redirect("/");
        }

        public IActionResult Login(string? returnUrl)
        {
            if (ViewData["returnUrl"]==null)
            {
                if (returnUrl is not null)
                {
                    ViewData["returnUrl"] = returnUrl;
                }
                else if (Request.Headers["Referer"].Any())
                {
                    var address = new Uri(Request.Headers["Referer"]);
                    if (address.PathAndQuery != "/Auth/Login")
                        ViewData["returnUrl"] = address.PathAndQuery;
                }
                else
                {
                    ViewData["returnUrl"] = "/";
                }
            }
            return View();
        }

        public IActionResult Register()
        {
            return View();
        }

        private void AddToCookie(string key, string value,DateTimeOffset exp)
        {
            Response.Cookies.Append(key, value, new CookieOptions { Expires = exp, HttpOnly = true });
        }

    }
}

namespace Project.Controllers
{
    public class CoursesController : Controller
    {
        ICourseService _courseService;
        IEnrollmentService _enrollmentService;
        public CoursesController(ICourseService courseService,IEnrollmentService enrollmentService)
        {
            _courseService = courseService;
            _enrollmentService = enrollmentService;
        }
        [Authorize(Roles ="Student")]
        public ActionResult Create() {

            return View();

        }
        [HttpPost]
        public async Task<IActionResult> CreateAsync(CourseToAddDto course)
        {
            var id = HttpContext.User.Claims("id")[0];
            course.InstructorID = Guid.Parse(id);
            await _courseService.CreateCourseAsync(course);
            return RedirectToAction("Index");
        }

        [HttpPost]
        public IActionResult DeleteCourse(string courseId)
        {
            _courseService.DeleteCourse(Guid.Parse(courseId));
            return RedirectToAction("Index");
        }
        public IActionResult Index()
        {
            var courses = _courseService.GetAllCourses();
            if(HttpContext.User.Claims("id").Any())
            {
                if (HttpContext.User.Claims(ClaimTypes.Role)[0]=="Student")
                {
                    var alreadyEnrolledCourses = _courseService.GetCoursesByUser(Guid.Parse(HttpContext.User.Claims("id")[0])).Select(c=>c.ID);
                    courses = courses.Where(c => !alreadyEnrolledCourses.Contains(c.ID)).ToList();
                }

            }
            return View(courses);
        }
        [Route("/Course/{courseId}")]
        public IActionResult IndexCourse(string courseId)
        {
            var course = _courseService.GetCourseById(Guid.Parse(courseId));
            if (HttpContext.User.Claims("id").Any())
            {
                if (HttpContext.User.Claims(ClaimTypes.Role)[0] == "Admin" 
                    || (HttpContext.User.Claims(ClaimTypes.Role)[0] == "Instructor" 
                    && course.InstructorID== Guid.Parse(HttpContext.User.Claims("id")[0])))
                {
                    ViewData["Users"] =_enrollmentService.GetByCourseId(Guid.Parse(courseId));
                }
            }
            return View(course);
        }

        [Authorize]
        public IActionResult MyCourses()
        {
            ViewData["Title"] = HttpContext.User.Claims("username")[0] + "'s Courses";
            var role = HttpContext.User.Claims(ClaimTypes.Role)[0];
            IEnumerable<Course> courses=null;
            switch (role)
            {
                case "Instructor":
                    var insID = HttpContext.User.Claims("id")[0];
                    courses = _courseService.GetCoursesByInstructor(Guid.Parse(insID));
                    break;
                case "Admin":
                    courses=_courseService.GetAllCourses();
                    break;
                default:
                    var uID = HttpContext.User.Claims("id")[0];
                    courses = _courseService.GetCoursesByUser(Guid.Parse(uID));
                    break;
            }
            return View(courses);

        }


    }
}

