#include <iostream>
#include <vector>

using namespace std;

class Item {
public:
    string name;
    int value;
    int weight;
    Item() {};
    Item(string n, int v, int w) {
        name = n;
        value = v;
        weight = w;
    }
};

class Inventory {
public:
    vector<Item> items;
    void addItem(Item item) {
        items.push_back(item);
    }
    void removeItem(int index) {
        items.erase(items.begin() + index);
    }
    void tradeItem(int index1, int index2, Inventory& otherInventory) {
        Item item1 = items[index1];
        Item item2 = otherInventory.items[index2];
        items[index1] = item2;
        otherInventory.items[index2] = item1;
    }
};

int main() {
    Inventory playerInventory;
    Inventory storeInventory;

    // Add items to player inventory
    Item sword("Sword", 50, 5);
    Item potion("Healing Potion", 10, 1);
    playerInventory.addItem(sword);
    playerInventory.addItem(potion);

    // Add items to store inventory
    Item armor("Armor", 100, 10);
    Item elixir("Elixir", 25, 2);
    storeInventory.addItem(armor);
    storeInventory.addItem(elixir);

    // Trade item with store inventory
    playerInventory.tradeItem(0, 1, storeInventory);

    // Print player inventory
    cout << "Player Inventory:" << endl;
    for (int i = 0; i < playerInventory.items.size(); i++) {
        Item item = playerInventory.items[i];
        cout << item.name << ", value: " << item.value << ", weight: " << item.weight << endl;
    }

    // Print store inventory
    cout << endl << "Store Inventory:" << endl;
    for (int i = 0; i < storeInventory.items.size(); i++) {
        Item item = storeInventory.items[i];
        cout << item.name << ", value: " << item.value << ", weight: " << item.weight << endl;
    }

    return 0;
